function [Componentname]ParseRoute($segments)
{
       $vars = array();
       switch($segments[0])
       {
               case 'categories':
               {
                       $vars['view'] = 'categories';
               }
               case 'category':
               {
                       $vars['view'] = 'category';
                       $id = explode(':', $segments[1]);
                       $vars['id'] = (int) $id[0];
               } break;
               case 'article':
               {
                       $vars['view'] = 'article;
                       $id = explode(':', $segments[1]);
                       $vars['id'] = (int) $id[0];
               } break;
       }
       return $vars;
}
