function [Componentname]BuildRoute(&$query)
{
       $segments = array();
       if(isset($query['catid']))
       {
                $segments[] = $query['catid'];
                unset($query['catid']);
       };
       if(isset($query['id']))
       {
                $segments[] = $query['id'];
                unset($query['id']);
       };
       unset($query['view']);
       return $segments;
}
